package com.example.realtimedatabaseimport android.annotation.SuppressLintimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.appcompat.app.AlertDialogimport androidx.recyclerview.widget.RecyclerViewimport com.google.firebase.database.FirebaseDatabaseclass ChatingAdapter(val chatingList: ArrayList<ChatModal>, val context: Context):RecyclerView.Adapter<ChatingAdapter.ChatView>() {    class ChatView(itemView: View) : RecyclerView.ViewHolder(itemView) {       val setText:TextView = itemView.findViewById(R.id.setText)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ChatView {      val layout =LayoutInflater.from(context).inflate(R.layout.chating_layout,parent,false)       return ChatView(layout)    }    override fun getItemCount(): Int = chatingList.size    override fun onBindViewHolder(holder: ChatView, position: Int) {       holder.setText.text = chatingList[position].cheating        holder.setText.setOnLongClickListener {            delete(position)            true        }    }    @SuppressLint("NotifyDataSetChanged")    private fun delete(position: Int) {        val alertDialog = AlertDialog.Builder(context)        alertDialog.setTitle(chatingList[position].cheating)        alertDialog.setMessage("${chatingList[position].cheating} delete")        alertDialog.setNegativeButton("NO"){dialog,_->            dialog.cancel()        }        alertDialog.setPositiveButton("YES"){dialog,_->            FirebaseDatabase.getInstance().getReference("cheating Database")                .child(chatingList[position].childName!!)                .removeValue()            notifyDataSetChanged()            dialog.dismiss()        }        alertDialog.create()        alertDialog.show()    }}