package com.example.entertainment.Adapterimport android.annotation.SuppressLintimport android.content.Contextimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.LinearLayoutimport android.widget.TextViewimport androidx.core.content.ContextCompatimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.bumptech.glide.request.RequestOptionsimport com.example.entertainment.AlbumsActivitySecimport com.example.entertainment.ModalData.AlbumsDataimport com.example.entertainment.Rimport com.google.android.material.bottomsheet.BottomSheetDialogimport com.google.android.material.imageview.ShapeableImageViewclass AlbumsListAdapter(val context: Context, private var albumsList: MutableList<AlbumsData>): RecyclerView.Adapter<AlbumsListAdapter.LayoutTwoViewHolder>() {    val imageList = arrayOf(        R.drawable.img1, R.drawable.img2,        R.drawable.img3,R.drawable.live_logo,        R.drawable.img5, R.drawable.music_logo)    inner class LayoutTwoViewHolder(itemView: View): RecyclerView.ViewHolder(itemView){        val albumsIcon: ShapeableImageView = itemView.findViewById(R.id.albums_icon)        val albumsName: TextView = itemView.findViewById(R.id.albums_name)        val albumsItem : TextView = itemView.findViewById(R.id.albums_item)        val menuBtn :ShapeableImageView = itemView.findViewById(R.id.albums_menu)        val parentLayout: LinearLayout = itemView.findViewById(R.id.albums_layout)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): LayoutTwoViewHolder {        val layout =            LayoutInflater.from(parent.context).inflate(R.layout.albums_layout, parent, false)        return LayoutTwoViewHolder(layout)    }    override fun onBindViewHolder(holder: LayoutTwoViewHolder, position: Int) {        val album = albumsList[position]        holder.albumsName.text = album.name        holder.albumsItem.text = album.albumsCount.toString()        holder.menuBtn.setOnClickListener {            bottomSheet(position)        }        Glide.with(context)            .load(album.albumIcon)            .placeholder(imageList.random()) // Placeholder image            .into(holder.albumsIcon)        holder.parentLayout.setOnClickListener {            val intent = Intent(context, AlbumsActivitySec::class.java)            intent.putExtra("position", position)            ContextCompat.startActivity(context, intent, null)        }    }    override fun getItemCount(): Int {        return albumsList.size    }    @SuppressLint("InflateParams")    private fun bottomSheet(pos:Int){        val layoutView = LayoutInflater.from(context).inflate(R.layout.parent_botttomsheet_music,null,false)        val bottomSheetDialog = BottomSheetDialog(context)        bottomSheetDialog.setContentView(layoutView)        val icon = bottomSheetDialog.findViewById<ShapeableImageView>(R.id.parent_icon)        val title = bottomSheetDialog.findViewById<TextView>(R.id.parent_title)        val dep = bottomSheetDialog.findViewById<TextView>(R.id.parent_dep)        title?.text = albumsList[pos].name        dep?.text = albumsList[pos].albumsCount        if (icon != null) {            Glide.with(context)                .load(albumsList[pos].albumIcon)                .apply(                    RequestOptions()                        .placeholder(imageList.random())                        .centerCrop()                )                .into(icon)        }        bottomSheetDialog.show()    }}