package com.example.entertainment.Adapterimport android.app.Applicationimport android.app.NotificationChannelimport android.app.NotificationManagerimport android.os.Buildimport java.nio.channels.Channelclass ApplicationClass:Application() {    companion object{        const val Channel_ID = "channel1"        const val PLAY = "play"        const val NEXT = "next"        const val PREVIOUS = "previous"        const val EXIT = "exit"    }    override fun onCreate() {        super.onCreate()        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O){            val notificationChannel = NotificationChannel(Channel_ID,"New Player",NotificationManager.IMPORTANCE_HIGH)            notificationChannel.description = "this is a important channel for shown song!!"            val notificationManager = getSystemService(NOTIFICATION_SERVICE) as NotificationManager            notificationManager.createNotificationChannel(notificationChannel)        }    }}