package com.example.entertainment.Adapterimport android.content.Contextimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.LinearLayoutimport android.widget.TextViewimport androidx.core.content.ContextCompatimport androidx.recyclerview.widget.RecyclerViewimport com.example.entertainment.FolderActivitySecimport com.example.entertainment.MainActivty.SecondActivityVideoFloderimport com.example.entertainment.ModalData.FolderDataimport com.example.entertainment.Rclass FolderAdapterMusic(val context: Context, private var folderList: MutableList<FolderData>): RecyclerView.Adapter<FolderAdapterMusic.LayoutTwoViewHolder>() {    inner class LayoutTwoViewHolder(itemView: View): RecyclerView.ViewHolder(itemView){        val foldersIcon: ImageView = itemView.findViewById(R.id.folder_Image_Mus)        val folderName: TextView = itemView.findViewById(R.id.folder_Name_Mus)        val folderItem :TextView = itemView.findViewById(R.id.folder_itemCount_Mus)        val parentLayout: LinearLayout = itemView.findViewById(R.id.folder_LayoutParent_Mus)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): LayoutTwoViewHolder {        val layout = LayoutInflater.from(parent.context).inflate(R.layout.music_folder_layout, parent, false)        return LayoutTwoViewHolder(layout)    }    override fun onBindViewHolder(holder: LayoutTwoViewHolder, position: Int) {        holder.folderName.text = folderList[position].folderName        holder.folderItem.text = folderList[position].Countandfile        holder.parentLayout.setOnClickListener {            val intent = Intent(context, FolderActivitySec::class.java)            intent.putExtra("position", position)            ContextCompat.startActivity(context, intent, null)        }    }    override fun getItemCount(): Int {        return folderList.size    }}