package com.example.entertainment.Adapterimport android.annotation.SuppressLintimport android.content.Contextimport android.content.Intentimport android.net.Uriimport android.provider.MediaStoreimport android.text.format.DateUtilsimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.LinearLayoutimport android.widget.TextViewimport androidx.appcompat.view.menu.MenuView.ItemViewimport androidx.core.content.ContextCompatimport androidx.recyclerview.widget.GridLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.bumptech.glide.request.RequestOptionsimport com.example.entertainment.MainActivty.SecondActivityVideoFloderimport com.example.entertainment.MainFragment.MusicFragmentParentimport com.example.entertainment.ModalData.FolderImgDataimport com.example.entertainment.ModalData.FolderVideoimport com.example.entertainment.ModalData.VideoDataMadalimport com.example.entertainment.Rimport com.example.entertainment.VideoFragmentChild.VideosFolderFragmentimport java.io.Fileimport java.lang.Exceptionimport kotlin.properties.Delegatesclass FolderAdapterVideo(val context: Context, private var folderList: MutableList<FolderVideo>) :    RecyclerView.Adapter<FolderAdapterVideo.LayoutTwoViewHolder>() {    companion object {        val imgUri = ArrayList<FolderImgData>()    }    inner class LayoutTwoViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        val folderName: TextView = itemView.findViewById(R.id.folder_Name)        val folderItem: TextView = itemView.findViewById(R.id.folder_itemCount)        val parentLayout: LinearLayout = itemView.findViewById(R.id.folder_LayoutParent)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): LayoutTwoViewHolder {        val layout = LayoutInflater.from(parent.context).inflate(R.layout.folder_layoutview, parent, false)        return LayoutTwoViewHolder(layout)    }    override fun onBindViewHolder(holder: LayoutTwoViewHolder, position: Int) {        val list = folderList[position]        holder.folderName.text = list.name        holder.folderItem.text = list.videoCount        holder.parentLayout.setOnClickListener {            val intent = Intent(context, SecondActivityVideoFloder::class.java)            intent.putExtra("position", position)            ContextCompat.startActivity(context, intent, null)        }    }    override fun getItemCount(): Int = folderList.size}