package com.example.entertainment.Adapterimport android.content.ContentValuesimport android.content.Contextimport android.graphics.PorterDuffimport android.util.Logimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.LinearLayoutimport android.widget.TextViewimport android.widget.Toastimport androidx.core.content.ContextCompatimport androidx.recyclerview.widget.RecyclerViewimport com.example.entertainment.MainFragment.MusicFragmentParentimport com.example.entertainment.ModalData.BottomSheetModalimport com.example.entertainment.Rimport java.io.Fileclass ParentMenu(val context: Context, private var list: Array<BottomSheetModal>) : RecyclerView.Adapter<ParentMenu.ViewHolder>() {    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        val icon: ImageView = itemView.findViewById(R.id.parent_Menu_img)        val title :TextView = itemView.findViewById(R.id.parent_Menu_text)        val layout :LinearLayout = itemView.findViewById(R.id.LinearLayout_delete)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int):ViewHolder {        val layout = LayoutInflater.from(parent.context).inflate(R.layout.parentmenu_layout, parent, false)        return ViewHolder(layout)    }    override fun onBindViewHolder(holder:ViewHolder, position: Int) {        val Position = list[position]        holder.icon.setImageResource(Position.img)        holder.title.text = Position.name        if (position == 6){            holder.icon.setColorFilter(ContextCompat.getColor(context, R.color.red), PorterDuff.Mode.SRC_IN)        }    }    override fun getItemCount(): Int =  list.size}