package com.example.entertainment.Adapterimport android.annotation.SuppressLintimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.example.entertainment.ModalData.Musicimport com.example.entertainment.Rimport org.w3c.dom.Textclass PlayList_Drop(val context: Context, private var bottomList: MutableList<Music>):RecyclerView.Adapter<PlayList_Drop.viewHolder>() {   inner class viewHolder(itemView:View):RecyclerView.ViewHolder(itemView){       val title :TextView = itemView.findViewById(R.id.drop_up_textView)   }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): viewHolder {        return viewHolder(LayoutInflater.from(parent.context).inflate(R.layout.playlist_drop_list_view,parent,false))    }    override fun getItemCount(): Int = bottomList.size    override fun onBindViewHolder(holder: viewHolder, position: Int) {        holder.title.text = bottomList[position].title    }}