package com.example.entertainment.Adapterimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentManagerimport androidx.lifecycle.Lifecycleimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.example.entertainment.VideoFragmentChild.VideosFolderFragmentimport com.example.entertainment.VideoFragmentChild.VideosListFragmentclass ViedeoViewPager2Adapter(fragmentManager: FragmentManager, lifecycle: Lifecycle):    FragmentStateAdapter(fragmentManager,lifecycle) {    val fragmentlist = arrayOf(VideosListFragment(),VideosFolderFragment())    override fun getItemCount(): Int {        return fragmentlist.size    }    override fun createFragment(position: Int): Fragment {        return when(position){            0 -> {                VideosListFragment()            }            1-> {                VideosFolderFragment()            }            else ->{                Fragment()            }        }    }}