package com.example.entertainment.Adapterimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentManagerimport androidx.lifecycle.Lifecycleimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.example.entertainment.MusicFragmentChild.AlbumFragmentimport com.example.entertainment.MusicFragmentChild.FolderFragmentMusicimport com.example.entertainment.MusicFragmentChild.PlaylistsFragmentimport com.example.entertainment.MusicFragmentChild.SongsFragmentclass viewPagerAdapter2(fragmentManager: FragmentManager, lifecycle: Lifecycle) : FragmentStateAdapter(fragmentManager,lifecycle) {    val musicChalid = arrayOf(SongsFragment(),PlaylistsFragment(),FolderFragmentMusic(),AlbumFragment())    override fun getItemCount(): Int {        return musicChalid.size    }    override fun createFragment(position: Int): Fragment {        return when(position){            0 -> {                SongsFragment()            }            1 ->{                PlaylistsFragment()            }            2 ->{                FolderFragmentMusic()            }            3 -> {                AlbumFragment()            }            else -> {                Fragment()            }        }    }}