package com.example.whatsapp.ViewPageAdapterimport android.content.Contextimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.example.whatsapp.ChattingPageActivityimport com.example.whatsapp.Modal.UserModalimport com.example.whatsapp.Rimport com.example.whatsapp.databinding.ChatingLayoutBindingclass FriendAdapter(val userList: List<UserModal>, val context: Context):RecyclerView.Adapter<FriendAdapter.Viewholder>() {    class Viewholder(itemView: View):RecyclerView.ViewHolder(itemView){       val binding :ChatingLayoutBinding = ChatingLayoutBinding.bind(itemView)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): Viewholder {       return Viewholder(LayoutInflater.from(context).inflate(R.layout.chating_layout,parent,false))    }    override fun onBindViewHolder(holder: Viewholder, position: Int) {       holder.binding.chatingName.text = userList[position].name        holder.binding.chatinMessage.text = userList[position].country        Glide.with(context).load(userList[position].imageUri).placeholder(R.drawable.baseline_account_circle_24)            .into(holder.binding.chatingIcon)        holder.itemView.setOnClickListener {            val intent = Intent(context,ChattingPageActivity::class.java).apply {                putExtra("uid",userList[position].uid)                putExtra("image_key",userList[position].imageUri)                putExtra("name",userList[position].name)                putExtra("country",userList[position].country)            }            context.startActivity(intent)        }    }    override fun getItemCount(): Int = userList.size}