package com.example.whatsapp.ViewPageAdapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.whatsapp.Modal.MessageModalimport com.example.whatsapp.Rimport com.example.whatsapp.databinding.OtherUserLayoutBindingimport com.example.whatsapp.databinding.UserChattingLayoutBindingimport com.google.firebase.auth.FirebaseAuthclass MessageAdapter(val list: List<MessageModal>,val context: Context):RecyclerView.Adapter<RecyclerView.ViewHolder>() {    var ITEM_SENT = 1    var ITEM_RECETVE = 2    inner class SentViewHolder(view:View):RecyclerView.ViewHolder(view){        val binding = UserChattingLayoutBinding.bind(view)    }    override fun getItemViewType(position: Int): Int {        return if (FirebaseAuth.getInstance().uid == list[position].senderId) ITEM_SENT else ITEM_RECETVE    }    inner class ReceiverViewHolder(view:View):RecyclerView.ViewHolder(view){        val binding = OtherUserLayoutBinding.bind(view)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): RecyclerView.ViewHolder {         if (viewType == ITEM_SENT){            return  SentViewHolder(LayoutInflater.from(context).inflate(R.layout.user_chatting_layout,parent,false))         }else{             return ReceiverViewHolder(LayoutInflater.from(context).inflate(R.layout.other_user_layout,parent,false))         }    }    override fun onBindViewHolder(holder: RecyclerView.ViewHolder, position: Int) {        val message = list[position]        if (holder.itemViewType == ITEM_SENT){            val viewHolder = holder as SentViewHolder            viewHolder.binding.sentText.text = message.message        }else{            val viewHolder = holder as ReceiverViewHolder            viewHolder.binding.recetveTExt.text = message.message        }    }    override fun getItemCount(): Int = list.size}