package com.example.whatsapp.ViewPageAdapterimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentManagerimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.example.whatsapp.Fragment.ChatFragmentimport com.example.whatsapp.Fragment.FriendFragmentimport com.example.whatsapp.Fragment.StoryFragmentclass ViewPagerAdapter(fragmentManager:FragmentManager, lifecycle:androidx.lifecycle.Lifecycle):FragmentStateAdapter(fragmentManager,lifecycle) {    private val fragmentList = arrayOf(FriendFragment(),ChatFragment(),StoryFragment())    override fun getItemCount(): Int = fragmentList.size    override fun createFragment(position: Int): Fragment {        when(position){            0 ->{                return FriendFragment()            }            1 ->{                return ChatFragment()            }            2 ->{                return  StoryFragment()            }            else ->{                return  Fragment()            }        }    }}